pkg update -y && pkg upgrade -y && pkg install zsh neofetch figlet ruby toilet proot-distro -y && gem install lolcat && chsh -s zsh && rm -f ~/.zshrc && echo -e '\n# ========== SHELL V0IC32 STYLED ========== \nclear\n\n# Animação simulada\nfor i in "." ".." "..." "...."; do echo -ne "\e[1;34mCarregando shell$i\r"; sleep 0.3; done; echo -e "\n"\n\n# Banner estiloso\nfiglet -f slant "v0ic32" | lolcat\n\n# Frases com estilo\necho -e "\e[38;5;208m╔════════════════════════════════════════════╗\e[0m"\necho -e "\e[1;32m║  Bem-vindo ao seu terminal custom, v0ic32  ║\e[0m"\necho -e "\e[1;35m║  O poder está no shell.                    ║\e[0m"\necho -e "\e[1;36m║  Confie no processo, ame o terminal.       ║\e[0m"\necho -e "\e[38;5;208m╚════════════════════════════════════════════╝\e[0m"\n\nsleep 0.8\n\n# Neofetch com logo do Arch e cores personalizadas\necho -e "\e[1;33mColetando informações do sistema...\e[0m"\nsleep 0.5\nneofetch --ascii_distro arch_small --colors 6 5 3 2 4 1\n\n# ========== PROMPT PERSONALIZADO ==========\n# Exibe usuário, host, diretório, hora com estilo\nexport PROMPT='\''%F{magenta}%n@%m%f %F{cyan}%~%f %F{yellow}[%D{%H:%M:%S}]%f %F{green}➤%f '\''\n' >> ~/.zshrc && echo -e "\n\e[1;32mConfiguração finalizada com sucesso, v0ic32. Reinicie o Termux e aproveite seu terminal estiloso.\e[0m"